import { Box, Text, Button, Stack } from "@chakra-ui/react";
import React, { useState } from "react";
import { useDrop } from "react-dnd";

const DropArea = () => {
	const [board, setBoard] = useState([]);
	const [indices, setIndices] = useState()

	const [{ isOver, canDrop }, drop] = useDrop(() => ({
		accept: "behaviour",
		drop: (item) => addToBoard(item),
		collect: (monitor) => ({
			isOver: monitor.isOver(),
			canDrop: monitor.canDrop(),
		})
	}));

	const addToBoard = (item) => {
		console.log(item.obj._id);
		if (!board.some((p) => p._id === item.obj._id)) {
			setBoard((board) => [...board, item.obj]);
		}
	};

	const handleReorder = (dragIndex, hoverIndex) => {
		const dragged = board[dragIndex];
		const newBoard = [...board];
		newBoard.splice(dragIndex, 1);
		newBoard.splice(hoverIndex, 0, dragged);
		setBoard(newBoard);
	}

	return (
		board.length > 0 ? (
			<Box
				ref={drop}
				width={400}
				height={500}
				border='1px solid'
				position='relative'
				maxHeight={500}
				overflowY='auto'
				justifyContent='center'
			>
				<Stack
					py={4}
					direction="column"
					paddingLeft={5}
					paddingRight={5}
				>
					{board.map((p, index) => (
						<Button
							backgroundColor='blue.200'
							height={10}
							width='auto'
							// draggable
							// onDragStart={(e) => {
							// 	e.dataTransfer.setData('text/plain', index);
							// }}
							// onDragOver={(e) => {
							// 	e.preventDefault();
							// }}
							// onDrop={(e) => {
							// 	const dragIndex = parseInt(e.dataTransfer.getData('text/plain'), 10);
							// 	const hoverIndex = index;
							// 	if (dragIndex != hoverIndex) {
							// 		handleReorder(dragIndex, hoverIndex);
							// 	}
							// }}
						>
							<Text
								fontSize='xl'
							>
								{p.rulename}
							</Text>
						</Button>
					))}
				</Stack>
			</Box>
		) : (
			<Box
				ref={drop}
				width={400}
				height={500}
				border='1px solid'
				position='relative'
				justifyContent='center'
				alignItems='center'
			>
				<Text
					textAlign="center"
				>Drop Here</Text>
			</Box>
		)
	)
};

export default DropArea;




