import React from "react";
import { Text } from "@chakra-ui/react";
import { useDrag } from "react-dnd";

function Box({ obj }) {
    const [{ isDragging }, drag] = useDrag(() => ({
        type: "behaviour",
        item: { id: obj._id },
        collect: (monitor) => ({
            isDragging: !!monitor.isDragging(),
        }),
    }));

    return (
        <Text
            ref={drag}
            color='white'
            fontSize='xl'
        >
            {obj.rulename}
        </Text>
    );
}

export default Behaviour;
