import React from "react";
import { Text, Button } from "@chakra-ui/react";
import { useDrag } from "react-dnd";

function Behaviour({ obj }) {
	const [{ isDragging }, drag] = useDrag(() => ({
		type: "behaviour",
		item: { id: obj._id },
		collect: (monitor) => ({
			isDragging: !!monitor.isDragging(),
		}),
	}));

	return (
		<Button
			ref={drag}
		>
			<Text
				fontSize='xl'
				style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          margin: 'auto',
          width: '100%',
          height: '100%',
          textAlign: 'center',
        }}
			>
				{obj.rulename}
			</Text>
		</Button>
	);
}

export default Behaviour;
