import { Flex, Text, Button } from "@chakra-ui/react";
import React, { useState } from "react";
import { useDrop } from "react-dnd";

const DropArea = () => {
	const [board, setBoard] = useState([]);

	const [{ isOver }, drop] = useDrop(() => ({
		accept: "behaviour",
		drop: (item) => addToBoard(item),
		collect: (monitor) => ({
			isOver: !!monitor.isOver(),
		})
	}));

	const addToBoard = (obj) => {
			console.log(obj);
			setBoard((board) => [...board, obj]);
	};

	return (
		<Flex
			ref={drop}
			style={{
        width: '600px',
        height: '500px',
        border: '1px solid #000',
        position: 'relative',
        justifyContent: 'center',
        alignItems: 'center',
      }}
		>
			{board.length > 0 ? (
					board.map((p) => (
						<Button backgroundColor='blue.200'>
							<Text fontSize='xl'>{p.rulename}</Text>
						</Button>
					))
			) : (
				<>
					<Text
						textAlign="center"
					>Drop Here</Text>
				</>
			)}
		</Flex>
	)
};

export default DropArea;




